-- ----------------------------
-- Table structure for books
-- ----------------------------
DROP TABLE IF EXISTS `books`;
CREATE TABLE `books`
(
    `Id`                 int(11)        NOT NULL,
    `Title`              varchar(200)   NOT NULL,
    `Author`             varchar(200)   NOT NULL,
    `CategoryId`         int(11)        NOT NULL,
    `UnitPrice`          decimal(19, 4) NOT NULL,
    `Clicks`             int(11)        NOT NULL,
    `PublisherId`        int(11)        NOT NULL,
    `PublishDate`        datetime       NOT NULL,
    `ISBN`               varchar(50)    NOT NULL,
    `WordsCount`         int(11)        NOT NULL,
    `ContentDescription` mediumtext,
    `AurhorDescription`  mediumtext,
    `EditorComment`      mediumtext,
    `TOC`                mediumtext,
    PRIMARY KEY (`Id`),
    KEY `fk_book_cate` (`CategoryId`),
    KEY `fk_book_publishers` (`PublisherId`),
    CONSTRAINT `fk_book_cate` FOREIGN KEY (`CategoryId`) REFERENCES `categories` (`Id`),
    CONSTRAINT `fk_book_publishers` FOREIGN KEY (`PublisherId`) REFERENCES `publishers` (`Id`)
) ENGINE = InnoDB
  DEFAULT CHARSET = utf8
  ROW_FORMAT = DYNAMIC;

