INSERT INTO `books`
VALUES ('5592', '微软XML解决方案', '（美）斯特姆 著 飞天工作室译', '22', '46.0000', '0', '3', '2001-06-01 00:00:00', '9787111088400', '0',
        '本书详细讨论了 XML基本内容、XSL文档的使用及XML在企业数字神经系统、分布式系统中的众多应用。书中涉及了众多的XML开发工具，为开发人员提供了大量的构造网页及将 XML深入到企业内部的源代码。', '', '',
        '一，企业中的XML\r<br>二，标记语言\r<br>三，XML文件结构\r<br>四，文件类型定义入门\r<br>五，实体和其他组件\r<br>六，XML名称空间，XPath,XPointer 和XLink\r<br>七，XML大纲\r<br>八，SOAP\r<br>九，BizTalk\r<br>十，windows DNA概述\r<br>十一，XML文档对象模型\r<br>十二，用XSL和CSS表示XML\r<br>十三，创建动态用户服务组件\r<br>十四，事务服务组件\r<br>十五，数据服务组件和XML\r<br>十六，Microsoft BizTalk Server 2000\r<br>');
