INSERT INTO `books`
VALUES ('5910', 'VB.NET数据库入门经典(第2版)', '（美）威利斯（Willis,T.）  著，闫英，曹蓉蓉  译', '16', '78.0000', '0', '1',
        '2006-11-01 00:00:00', '9787302135913', '0',
        'VB.NET是目前最流行的编程语言之一，是创建.NET应用程序的最简单效率也最高的工具。本书介绍了如何利用VB.NET的强大功能编写可以在整个企业应用中使用的实用数据库程序。<br>    本书通过丰富的示例和具体指导，阐述了显著提高已有数据库的性能和可伸缩性的方式，探讨了在应用程序中高效地存取和处理数据的方式，研究了如何最好地利用查询、视图和存储过程。',
        '', '',
