INSERT INTO `books`
VALUES ('5911', 'Visual Basic开发ERP系统实例导航（附光盘）——中小企业信息化建设指导丛书', '求是科技 编著', '16', '49.0000', '0', '2',
        '2005-01-01 00:00:00', '7115129827', '699000',
        '本书介绍了一个较为完整、有良好实用性的企业资源计划(ERP)应用系统的开发过程。首先让读者了解和掌握一个典型的ERP系统应该设计哪些功能，其次是采用Visual Basic的哪些技术手段来实现这些功能，使读者在系统设计与技术使用两个方面都有所收获。<br>　　该ERP系统主要包括：工程管理子系统、客户管理子系统、采购管理子系统、销售管理子系统、库存管理子系统、人力管理子系统和系统管理子系统等诸多典型功能。对于这些系统功能，书中都一一给以设计与实现的详细介绍。此外，为了让读者更好地理解和掌握ERP系统的构建方法，本书还介绍了一些ERP的基本理论。<br>　　本书配套光盘中提供了系统完整的源代码，并给出了系统配置过程的AVI动画演示，方便读者能顺利配置和运行本书程序。本书适于掌握了基础的Visual Basic编程技术，对ERP应用系统感兴趣的开发人员、自学人员参考使用。',
        '<br>', '<br>',
