DROP TABLE IF EXISTS `users`;
CREATE TABLE `users`
(
    `Id`          int(11)      NOT NULL,
    `Name`        varchar(50)  NOT NULL,
    `Address`     varchar(200) NOT NULL,
    `Phone`       varchar(100) NOT NULL,
    `Mail`        varchar(100) NOT NULL,
    `UserStateId` int(11)      NOT NULL,
    `IsOnLine`    int(11) DEFAULT NULL,
