    KEY `fk_user_userrole` (`UserRoleId`),
    KEY `fk_user_userstate` (`UserStateId`),
    CONSTRAINT `fk_user_userrole` FOREIGN KEY (`UserRoleId`) REFERENCES `userroles` (`Id`),
    CONSTRAINT `fk_user_userstate` FOREIGN KEY (`UserStateId`) REFERENCES `userstates` (`Id`)
) ENGINE = InnoDB
  DEFAULT CHARSET = utf8
  ROW_FORMAT = DYNAMIC;

